import styles from "./NavBar.module.css";
import { Link } from "react-router-dom";
import { BsList, BsXLg } from 'react-icons/bs';
import { useEffect, useState } from "react";

export default function NavBar() {
  const colorBtn = 'white'
  const sizeBtn = 36

  const [menu, setMenu] = useState(<BsList color={colorBtn} size={sizeBtn}/>);
  const [isOpen , setIsOpen] = useState(false)
  function openMenu() {
    setMenu(menu.type.name === 'BsXLg' ? <BsList  color={colorBtn} size={sizeBtn}/> : <BsXLg  color={colorBtn}/>);

    
  } 

  // Verificar se está em um dispositivo móvel
  const [isMobile, setIsMobile] = useState(false);
  const checkMobile = () => {
    setIsMobile(window.innerWidth <= 900);
    console.log(isMobile)
    if(isMobile){
       setIsOpen(true)
    }
   
  };

 
 
  // Verificar se a janela é alterada
  useEffect(() => {
    checkMobile();
    window.addEventListener("resize", checkMobile);
    return () => window.removeEventListener("resize", checkMobile);
  },[] );

  return (
    <div className={styles.div}>
      <main>
        <nav>
          <h1>BusinessHere</h1>
          {isMobile && (
            <button className={styles.MenuBtn} onClick={openMenu}>
              {menu}
            </button>
          )}
          <ul className={`${menu.type.name === 'BsXLg' || isOpen === true ? styles.menuH : ''}`}
            >
              <Link onClick={openMenu} className="btn" to="/">Inicio</Link>
              <Link onClick={openMenu} className="btn" to="/cadastrar">Cadastrar</Link>
              <Link onClick={openMenu} className="btn" to="/visualizar">VisuaLizar</Link>
            </ul>
        </nav>
      </main>
    </div>
  );
}
