import { useEffect, useState } from "react";

export default function CheckMobile() {
  const [isMobile, setIsMobile] = useState(false);

  const checkMobiles = () => {
    setIsMobile(window.matchMedia("(max-width: 900px)").matches);
    console.log(isMobile);
  };
}
