import { Link } from "react-router-dom";
import style from "./Visualizar.module.css";

import { BsLink, BsPencil, BsTrashFill } from "react-icons/bs";

import { useEffect, useState, useCallback } from "react";
import Loading from "../layout/Loading";
import Astronaut from "../img/astronaut.svg";
import CheckMobile from "../Funcoes/CheckMobile";
import databaseLocal from "../bd/db.json";

import Container from "../layout/Container";

export default function Visualizar() {
  const checkMobile = useCallback(CheckMobile, []);
  const isMobile = checkMobile();
  const [database, setDatabase] = useState(null); // Estado para armazenar os dados

  const fetchData = async () => {
    try {
      const url =
        process.env.REACT_APP_API_URL || "http://localhost:5000/empresas";

      const response = await fetch(url, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

      const data = await response.json();
      setDatabase(data);
    } catch (error) {
      //console.log("Erro ao obter os dados", error);
      setDatabase(databaseLocal.empresas);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  function removeEmpresa(id) {
    fetch(`http://localhost:5000/empresas/${id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((resp) => resp.json())
      .then(() => {
        setDatabase(database.filter((projetct) => projetct.id !== id));
      })
      .catch((err) => console.log(err));
  }

  const remove = (id) => {
    console.log(id);
    removeEmpresa(id);
  };

  /**Atualizar  dados*/

  useEffect((data) => {
    if (dataId) {
      fetch(`http://localhost:5000/empresas/${dataId}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      })
        .then((response) => response.json())
        .then((dataReponse) => {
          setdataApi(dataReponse);
        })
        .catch((err) => console.log(err));
    }
  }, [dataId]);

  function editarApi(e) {
    fetch(`http://localhost:5000/empresas/${dataId}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        setFormData(data);
      })
      .catch((err) => console.log(err));
  }

  /*
arrumar as  variaveis com os campo e valores
mostra os valores no input
mostra se os botao estiver

*/

  return (
    <Container>
      <div className={style.father}>
        <div className={style.texto}>
          <h1>Empresas cadastradas.</h1>
          <p>Acesse o link abaixo para ter informações completas.</p>
          <p>Aqui estão as empresas cadastradas</p>
        </div>

        {database ? ( // Se os dados foram obtidos com sucesso
          <div className={style.tablediv}>
            <table>
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Nomes</th>
                  {!isMobile && <th>Localidade</th>}
                  <th>Setor</th>
                  <th>Editar</th>
                  <th>Excluir</th>
                </tr>
              </thead>
              <tbody>
                {database.map(({ id, nome, localidade, setor }) => (
                  <tr key={id}>
                    <td>{id}</td>
                    <td>
                      <Link
                        to={`/visualizar/${id}/${nome}`}
                        key={id}
                        title="Acesse aqui"
                      >
                        <BsLink className={style.icon} title="Acesse aqui" />
                        {nome}
                      </Link>
                    </td>
                    {!isMobile && <td>{localidade}</td>}

                    <td>{setor}</td>
                    <td className={style.btnCrud}>
                      <button>
                        <BsPencil />
                      </button>
                    </td>
                    <td className={style.btnCrud}>
                      <button onClick={() => remove(id)}>
                        <BsTrashFill />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            {database.length === 0 && (
              <>
                <h1>
                  <img src={Astronaut} alt="figurinha" />
                </h1>
                <p>Sem cadastros...</p> <Loading />
              </>
            )}
          </div>
        ) : (
          <>
            <p>Carregando dados...</p>
            <Loading />
          </>
        )}
      </div>
    </Container>
  );
}
