import style from "./Cadastrar.module.css";
import Container from "../layout/Container";
import Input from "../layout/Input";
import dblocal from "../bd/db.json";
import { useEffect, useState } from "react";

export default function Cadastrar({ dataId }) {
  // Define o estado inicial do formulário
  const [formData, setFormData] = useState({
    id: "",
    nome: "",
    localidade: "",
    site: "",
    setor: "",
    visao: "",
    fundador: "",
    anofundação: "",
    valores: "",
    produtos: "",
    imagem: "",
  });
  const [dataApi, setdataApi] = useState({});

  const [btnText, setBtnTexto] = useState("Enviar");

  // Atualiza o estado do formulário quando um campo é alterado
  const handleChange = (event) => {
    if (event.target.value === "site") {
      setFormData({ ...formData, [event.target.name]: event.target.value });
    } else {
      const capitalizedValue =
        event.target.value.charAt(0).toUpperCase() +
        event.target.value.slice(1);
      setFormData({ ...formData, [event.target.name]: capitalizedValue });
    }
  };

  // Manipula o envio do formulário
  const handleSubmitForm = async (event) => {
    try {
      setBtnTexto("Enviando...");
      // Envia uma requisição POST para a URL especificada
      const response = await fetch("http://localhost:5000/empresas", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      // Trata a resposta da requisição (pode ser necessário ajustar de acordo com a API)
      const data = await response.json();

      // Atualiza o estado do formulário com os dados recebidos
      setFormData(data);
      dblocal.append(formData);
    } catch (error) {
      console.log(error);
    }
  };

  /**Atualizar  dados*/
  useEffect(() => {
    fetch(`http://localhost:5000/empresas/${dataId}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((dataReponse) => {
        setdataApi(dataReponse);
      })
      .catch((err) => console.log(err));
  }, [dataId]);

  function editarApi(e) {
    e.preventDefault();
    fetch(`http://localhost:5000/empresas/${dataId}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        setFormData(data);
      })
      .catch((err) => console.log(err));
  }

  return (
    <Container>
      <div className={style.father}>
        {!dataApi && <h1>Cadastre novas empresas</h1>}
        {dataapi &&dataApi.map(({ nome }) => {
          <Input
            text="Nome da empresa:"
            placeholder="Digite o nome da empresa..."
            id="nome"
            type="text"
            name="nome"
            value={nome}
            handleChange={handleChange}
          />;
        })}
        <form onSubmit={handleSubmitForm} className={style.form}>
          {/* Componente de entrada de texto para o nome da empresa */}
          <Input
            text="Nome da empresa:"
            placeholder="Digite o nome da empresa..."
            id="nome"
            type="text"
            name="nome"
            value={formData.nome}
            handleChange={handleChange}
          />
          {/* Componente de entrada de texto para os site */}
          <Input
            text="site:"
            placeholder="Digite os site "
            type="text"
            id="site"
            name="site"
            value={dataApi ? dataApi.site : formData.site}
            handleChange={handleChange}
          />
          {/* Componente de entrada de texto para o local */}
          <Input
            text="Local:"
            placeholder="Digite o local..."
            type="text"
            id="localidade"
            name="localidade"
            value={dataApi ? dataApi.localidade : formData.localidade}
            handleChange={handleChange}
          />
          {/* Componente de entrada de texto para o setor */}
          <Input
            text="Setor:"
            type="text"
            placeholder="Digite o setor..."
            id="setor"
            name="setor"
            value={dataApi ? dataApi.setor : formData.setor}
            handleChange={handleChange}
          />
          {/* Componente de entrada de texto para a visão */}
          <Input
            text="Visão:"
            type="text"
            placeholder="Digite a visão..."
            id="visao"
            name="visao"
            value={dataApi ? dataApi.visao : formData.visao}
            handleChange={handleChange}
          />
          {/* Componente de entrada de texto para a data de fundação */}
          {/* arruma esse campo de data e fundador*/}
          <Input
            text="Fundador:"
            type="text"
            placeholder="Digite o ano da fundação..."
            id="Fundador"
            name="Fundador"
            value={dataApi ? dataApi.fundador : formData.fundador}
            handleChange={handleChange}
          />
          <Input
            text="data de  fundação"
            type="date"
            placeholder="Digite o ano da fundação..."
            id="anofundação"
            name="anofundação"
            value={dataApi ? dataApi.anofundação : formData.anofundação}
            handleChange={handleChange}
            className={style.data}
          />
          <Input
            text="valores:"
            type="text"
            placeholder="Digite os valores..."
            id="valores"
            name="valores"
            value={dataApi ? dataApi.valores : formData.valores}
            handleChange={handleChange}
          />
          <Input
            text="produtos:"
            type="text"
            placeholder="Digite os produtos..."
            id="produtos"
            name="produtos"
            value={dataApi ? dataApi.produtos : formData.produtos}
            handleChange={handleChange}
          />
          {/* Botão de envio do formulário */}
          {dataApi && (
            <button onClick={editarApi} className={style.btn}>
              {btnText}
            </button>
          )}
          {!dataApi && <button className={style.btn}>{btnText}</button>}
        </form>
      </div>
    </Container>
  );
}
